import { Chain, Polkadot, Edgeware, Kusama } from './chains'
import { IdentityTypes } from 'edgeware-node-types/dist/identity'
import { SignalingTypes } from 'edgeware-node-types/dist/signaling'
import { TreasuryRewardTypes } from 'edgeware-node-types/dist/treasuryReward'
import { VotingTypes } from 'edgeware-node-types/dist/voting'
import { Metadata, TypeRegistry } from '@polkadot/types'
import { Registry } from '@polkadot/types/types'

export type Network = {
  name: string,
  chain: Chain,
  genesisHash: string,
  specVersion: number,
  ss58Format?: number,
  tokenDecimals?: number,
  tokenSymbol?: string,
  registry: Registry,
  hasMetadata: boolean,
  rpcServer: string,
  types: {},
  txExplorer: string
}

const alexander = {
  name: 'Alexander',
  chain: Polkadot,
  genesisHash: '0xdcd1346701ca8396496e52aa2785b1748deb6db09551b72159dcb3e08991025b',
  specVersion: 112,
  ss58Format: 42,
  tokenDecimals: 15,
  tokenSymbol: 'DOT',
  types: {},
  rpcServer: 'wss://poc3-rpc.polkadot.io/',
  txExplorer: 'https://polkascan.io/pre/alexander/system/extrinsic/',
  // tslint:disable-next-line:max-line-length
  metaCalls: ''
}

const kusama = {
  name: 'Kusama',
  chain: Kusama,
  genesisHash: '0xb0a8d493285c2df73290dfb7e61f870f17b41801197a149ca93654499ea3dafe',
  specVersion: 1030,
  ss58Format: 2,
  tokenDecimals: 12,
  tokenSymbol: 'KSM',
  types: {
    Keys: 'SessionKeys5'
  },
  rpcServer: 'wss://kusama-rpc.polkadot.io/',
  txExplorer: 'https://polkascan.io/pre/kusama/extrinsic',
  // tslint:disable-next-line:max-line-length
  metaCalls: ''
}

const edgeware = {
  name: 'Edgeware',
  chain: Edgeware,
  genesisHash: '0xda60008770287b5eb4f9b5241317ac4c7e42c3c3d8e85fbbfa2a88705394d75e',
  specVersion: 20,
  ss58Format: 42,
  tokenDecimals: 18,
  tokenSymbol: 'EDG',
  types: {
    Keys: 'SessionKeys3',
    ValidatorPrefs: 'ValidatorPrefsTo196',
    ...IdentityTypes,
    ...SignalingTypes,
    ...TreasuryRewardTypes,
    ...VotingTypes
  },
  rpcServer: 'wss://testnet4.edgewa.re/',
  txExplorer: 'https://polkascan.io/pre/edgeware-testnet/system/extrinsic/',
  metaCalls: ''
}

export const DEFUALT_NETWORK = kusama.name

export const networksMap: Map<string, Network> = new Map(
  // tslint:disable-next-line:max-line-length
  [kusama, edgeware, alexander].map(({ name, chain, genesisHash, metaCalls, specVersion, ss58Format, tokenDecimals, tokenSymbol, types, rpcServer, txExplorer }): [string, Network] => {
    let metadata: Metadata | undefined
    const registry = new TypeRegistry()
    registry.register(types || {})

    if (metaCalls) {
      metadata = new Metadata(registry, Buffer.from(metaCalls, 'base64'))
    }

    return [genesisHash, {
      genesisHash,
      hasMetadata: !!metadata,
      chain,
      types,
      name,
      registry,
      specVersion,
      ss58Format,
      tokenDecimals,
      tokenSymbol,
      rpcServer,
      txExplorer
    }]
  })
)

let byName = {}
networksMap.forEach(network => { byName[network.name] = network })
export const networks: {[name: string]: Network} = byName

const UNKNOWN_NETWORK: Network = {
  hasMetadata: false,
  chain: Polkadot,
  genesisHash: '',
  name: 'Unknown network',
  registry: new TypeRegistry(),
  specVersion: 0,
  ss58Format: 42,
  tokenDecimals: 0,
  types: {},
  tokenSymbol: 'UNIT',
  rpcServer: '',
  txExplorer: ''
}

export function findNetwork (genesisHash?: string | null): Network {
  return networksMap.get(genesisHash || '') || UNKNOWN_NETWORK
}
